                bold: false,
                fillInfo: "#F2F2F2"
            });
            inputs[2] = CreditPositionSVG.Input({
                id: "maturity",
                input: "Maturity",
                info: getReadableDateString(params.maturity),
                xTitle: "362",
                yTitle: "500",
                xInfo: "362",
                yInfo: "400",
                rotate: true,
                bold: true,
                fillInfo: LIQUIDITY_COLOR
            });
            inputs[3] = tokenIdInput;
            uri = constructTokenSVG(inputs, LIQUIDITY_COLOR, LIQUIDITY_TITLE);
